//! Unit tests for configuration builder module
//!\n//! These tests were moved from src/config/builder.rs to maintain\n//! separation between implementation and test code.\n\nuse router_flood::config::builder::ConfigBuilder;\nuse router_flood::config::ProtocolMix;\n\n#[test]\nfn test_valid_configuration() {\n    let config = ConfigBuilder::new()\n        .target_ip(\"192.168.1.1\")\n        .target_ports(vec![80, 443])\n        .threads(4)\n        .packet_rate(100)\n        .build();\n    \n    assert!(config.is_ok());\n}\n\n#[test]\nfn test_invalid_ip() {\n    let config = ConfigBuilder::new()\n        .target_ip(\"8.8.8.8\") // Public IP should fail\n        .build();\n    \n    assert!(config.is_err());\n}\n\n#[test]\nfn test_protocol_mix_validation() {\n    let invalid_mix = ProtocolMix {\n        udp_ratio: 0.5,\n        tcp_syn_ratio: 0.3,\n        tcp_ack_ratio: 0.3, // Total > 1.0\n        icmp_ratio: 0.1,\n        ipv6_ratio: 0.0,\n        arp_ratio: 0.0,\n    };\n    \n    let config = ConfigBuilder::new()\n        .protocol_mix(invalid_mix)\n        .build();\n    \n    assert!(config.is_err());\n}\n"